<style scoped>
svg {
    width: 100%;
    height: auto;
}
#logo.animate {
    --bounce-distance: -50px;
    animation: bounce 1.5s ease-in-out infinite;
}
#robot {
    --bounce-distance: -50px;
    animation: bounce 1.5s infinite;
}
#eyes {
    /*fill: purple !important;*/
    stroke: #323567;
}
#eyes.normal {
    transition: all 1.5s;
    animation: normal 1s ease-in-out infinite;
}
#eyes.talk {
    animation:
        side 0.3s 1 ease-in,
        squint 0.5s 1 0.3s ease-in,
        joy 0.5s 1 0.8s ease-in,
        side 0.3s 1.3s ease-in,
        joy 0.5s 1 1.6s ease-in;
}
#eyes.squint {
    animation: squint 1.5s ease-in forwards;
}
#eyes.joy {
    stroke: #d8dee8;
    animation: joy 0.5s ease-in forwards;
}

@keyframes normal {
    0% {
        transform: translate(0px, 0px);
        stroke-width: 0px;
    }
    50% {
        transform: translate(0px, -10px);
        stroke-width: 10px;
    }
    100% {
        transform: translate(0px, 0px);
        stroke-width: 0px;
    }
}

@keyframes joy {
    0% {
        transform: translate(0px, 0px);
        stroke-width: 0px;
    }
    50% {
        transform: translate(20px, -60px);
        stroke-width: 30px;
    }
    100% {
        transform: translate(0px, 0px);
        stroke-width: 0px;
    }
}

@keyframes side {
    0% {
        transform: translateX(0px);
    }
    50% {
        transform: translateX(40px);
    }
    100% {
        transform: translateX(0px);
    }
}

@keyframes squint {
    0% {
        stroke-width: 0px;
    }
    50% {
        stroke-width: 50px;
    }
    100% {
        stroke-width: 30px;
    }
}
</style>
